import { useRouter } from 'next/router';
import Header from './header';

const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #ccc',
  background: '#fafafa'
};

const router = useRouter();

const Layout = props => (
  <div style={layoutStyle}>
    <Header />
    <h1>{router.query.title}</h1>
    {props.children}
  </div>
);

export default Layout;
